import Carousel from 'react-multi-carousel';
import "react-multi-carousel/lib/styles.css";
import Countdown from 'react-countdown';
import { Button, Divider, Box, Typography, styled } from '@mui/material';

const responsive = {
    desktop: {
        breakpoint: { max: 3000, min: 1024 },
        items: 5,
    },
    tablet: {
        breakpoint: { max: 1024, min: 464 },
        items: 2,
    },
    mobile: {
        breakpoint: { max: 464, min: 0 },
        items: 1,
    }
};

const Component = styled(Box)`
    margin-top: 10px;
    background: #FFFFFF;
`;

const Deals = styled(Box)`
    display: flex;    
    padding: 10px 20px;
    justify-content:flex-start;
    align-items:center;
`
const DealsText = styled(Typography)`
    font-size: 20px;
    font-weight: 550;
    line-height: 32px;
    margin-right: 25px;
`

const Image = styled('img')({
    width: auto,
    height: 100,
});

const RenderTimer = styled(Box)(({ theme }) => ({
    [theme.breakpoints.down('sm')]: {
        display: 'none'
    }
}));

const Slide =({products})=>{

    const renderer = ({ hours, minutes, seconds }) => {
        return <RenderTimer variant="span">{hours} : {minutes} : {seconds}  Left</RenderTimer>;
    };

    return (
    <Component>
        <Deals>
            <DealsText>Deals of the day</DealsText>
            <Box style={{marginLeft:30,marginTop:2,fontSize:20}}>
            <Countdown  date={Date.now() + 5.04e+7} renderer={renderer} />
            </Box>
        </Deals>
        <Divider />
    <Carousel  swipeable={false}
    draggable={false}
    responsive={responsive}
    centerMode={true}
    infinite={true}
    autoPlay={true}
    autoPlaySpeed={10000}
    keyBoardControl={true}
    showDots={false}
    containerClass="carousel-container"
    // removeArrowOnDeviceType={["tablet", "mobile"]}
    dotListClass="custom-dot-list-style"
    itemClass="carousel-item-padding-40-px"
    >
    {
        products.map(product =>(
        <img src={product.url} alt="product" id={product.id} key={product.id} />
        )) 
    }
    </Carousel>
    </Component>
    ) 
}

export default Slide;
