import { AppBar, Toolbar, Box, styled,Typography,IconButton,Drawer, Icon } from '@mui/material';
import Search from './search';
import { Menu } from '@mui/icons-material';
import CustomButtons from './custom_buttons';
import { useState } from 'react';

const StyledHeader = styled(AppBar)`
    background: #FFA500;
    height: 55px;
`;

const Component = styled(Box)`
    margin-left: 12%;
    line-height: 0;
`;

const PlusImage = styled('img')({
    width: 21,
    height: 22,
    marginLeft: 4
})

const CustomButtonWrapper = styled(Box)(({ theme }) => ({
    margin: '0 5% 0 2%',
    [theme.breakpoints.down('md')]: {
        display:'none'
    }
    }))

    const MenuButton = styled(IconButton)(({ theme }) => ({
        display: 'none',
        [theme.breakpoints.down('sm')]: {
            display: 'block'
        }
    }));


const Header = () => {

    const [open, setOpen] = useState(false);

    const handleClose = () => {
        setOpen(false);
    }

    const handleOpen = () => {
        setOpen(true);
    }

return (
    <StyledHeader position="fixed">
            <Toolbar style={{minHeight:55}}>
            <MenuButton
                    color="inherit"
                    onClick={handleOpen}
                >
                <Menu />
                </MenuButton>
                <Component to='/'>
                    <Box>
                    <PlusImage src='./.././public/logo.png' />
                    </Box>
                </Component>
                <Search/>
                <CustomButtonWrapper>
                <CustomButtons/>
                </CustomButtonWrapper>
               
            </Toolbar>
        </StyledHeader>
)
}


export default Header;
