import Banner from "./banner";
import { Box,styled } from "@mui/material";
import { useEffect } from "react";
import { getProducts } from "../../redux/actions/productActions";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux/es/hooks/useSelector";
import Slide from "./Slide";

const StyleBox = styled(Box)`
padding:10px;
background:#F2F2F2;

`;
const Home= ()=>{
    const {products}=useSelector(state=>state.getProducts);
    const dispatch = useDispatch();
    useEffect(()=>{
     dispatch(getProducts()) 
    },[dispatch]);
    return (
    <>
    <StyleBox>
    <Banner topSection={true}/>
    </StyleBox>
    <Slide products={products} title='Deals of the day' showTimer={true} />
    <Slide products={products} title='Most Trending'  showTimer={false} />
    <Slide products={products} title='Our Recommendations' showTimer={false} />
    <StyleBox>
    <Banner topSection={false}/>
    </StyleBox>
    <Slide products={products} title='Electronics' showTimer={false} />
    <Slide products={products} title='Clothes' showTimer={false} />
    <Slide products={products} title='Sports' showTimer={false} />
    </>
    )
}

export default Home;
