import { Box, Typography,Button, styled } from '@mui/material';
import { ShoppingCart } from '@mui/icons-material';
import React, { useState, useContext } from 'react';
import LoginDialog from '../Login/login';
import DataContext  from '../../Context/dataprovider';
const Wrapper = styled(Box)`
    margin: 0 8% 0 4%;
    display: flex;
    width:26em;
    & > button,& > p,& > div {
        marginRight: 50px;
        marginLeft:20px;
        textDecoration: none;
        color: #FFFFFF;
        fontSize: 12px;
        alignItems: center;
    `;

    const Container = styled(Box)`
        display: flex;
        marginLeft:6px;
        `;


        const LoginButton = styled(Button)`
            color: #ffffff;
            background: #009933;
            textTransform: none;
            fontWeight: 600;
            borderRadius: 2;
            padding: 5px 40px;
            height: 35px;
            boxShadow: none;
            marginTop:8px;
            `;


    const CustomButtons = () => {

        const [open, setOpen] = useState(false);
    
        const openDialog = () => {
            setOpen(true);
        }
        const { account } = useContext(DataContext);

        return (
            <Wrapper>
                   {
                account ? <Typography>{account}</Typography>:
                    <LoginButton variant="contained" onClick={() => openDialog()}>Login</LoginButton>
                
            }
                <LoginButton variant="contained" onClick={() => openDialog()}>Login</LoginButton>
                <Typography style={{ marginTop: 3, width: 196 }}>Become a Seller</Typography>
                <Typography style={{ marginTop: 3, width:36 }}>More</Typography>
                <Container>
                    <ShoppingCart style={{marginLeft:19}}/>
                    <Typography style={{ marginLeft: 3 ,marginTop:3}}>Cart</Typography>
                </Container>
                <LoginDialog open={open} setOpen={setOpen}/>
            </Wrapper>
        )
    }
    
    export default CustomButtons;
