import { ButtonGroup, Button, styled } from "@mui/material";
import { useState } from "react";

const Component = styled(ButtonGroup)`
    margin-top: 30px;
`;

const StyledButton = styled(Button)`
    border-radius: 50%;
`;

const GroupButton = ({item}) => {

    const [ curr_quantity, setCounter ] = useState(item.quantity);

    const handleIncrement = () => {
        setCounter(curr_quantity => curr_quantity + 1 );
    };

    const handleDecrement = () => {
        setCounter(curr_quantity => curr_quantity - 1 );
    };

return (
    <Component>
        <StyledButton onClick={() => handleDecrement()} disabled={curr_quantity === 1}>-</StyledButton>
        <Button disabled>{curr_quantity}</Button>
        <StyledButton onClick={() => handleIncrement()}>+</StyledButton>
    </Component>
);

}
export default GroupButton;
